$dfrom = $_criteria["daterange"]->get_criteria_value("RANGE1");
$dto = $_criteria["daterange"]->get_criteria_value("RANGE2");
$rt = $_criteria["route"]->get_criteria_value("VALUE");
$op = $_criteria["operator"]->get_criteria_value("VALUE");
$loc = $_criteria["location"]->get_criteria_value("VALUE");
$ftm = $_criteria["fromTime"]->get_criteria_value("VALUE");
$ttm = $_criteria["toTime"]->get_criteria_value("VALUE");
$ipd = $_criteria["initialpred"]->get_criteria_value("VALUE");
$actonly = $_criteria["actonly"]->get_criteria_value("VALUE");



if ( strlen($ipd) > 0 )
    $ipd = "INITIALONLY";
else
    $ipd = "ALL";

$dfdy = substr($dfrom, 1,2);
$dfmn = substr($dfrom, 4,2);
$dfyr = substr($dfrom, 7,4);
$dtdy = substr($dto, 1,2);
$dtmn = substr($dto, 4,2);
$dtyr = substr($dto, 7,4);

$ifrom = mktime ( 0, 0, 0, $dfmn, $dfdy, $dfyr );
$ito = mktime ( 0, 0, 0, $dtmn, $dtdy, $dtyr );

$sql = "SET ISOLATION TO DIRTY READ";

$ds->Execute($sql) or print $ds->ErrorMsg(); 

$sql = "CREATE TEMP TABLE t_days ( day date, dtime datetime year to day ) with no log;";

$ds->Execute($sql) or print $ds->ErrorMsg(); 

$ptr = $ifrom;
while ( $ptr <= $ito )
{
    $dt = strftime ( "%d/%m/%Y", $ptr );
    $dtm = strftime ( "%Y-%m-%d", $ptr );

    $sql = "INSERT INTO t_days VALUES ( '".$dt."', '".$dtm."' );";
    $ds->Execute($sql) or print $ds->ErrorMsg(); 
    $ptr = $ptr + ( 24 * 60 * 60 );
};


$sql = "
SELECT t_days.day, t_days.dtime, route_code, service_code,
route.route_id, operator.operator_code operator_code,
service.service_id, publish_tt.pub_ttb_id pub_ttb_id,
publish_tt.runningno runningno,
event.event_code event_code, event.event_id, notes[1,1] over_midnight
FROM operator,cent_user,route,service, t_days, publish_tt,event_pattern,event
WHERE 1 = 1
AND (
operator.operator_id = cent_user.operator_id
OR cent_user.operator_id IS NULL )
AND cent_user.usernm = USER
AND operator.operator_id = route.operator_id
AND route.route_id = service.route_id
AND publish_tt.service_id = service.service_id
and publish_tt.evprf_id   = event_pattern.evprf_id
and event_pattern.event_id   = event.event_id
and t_days.day between service.wef_date and service.wet_date
and weekday(t_days.day) between rpdy_start and rpdy_end
and ( current > extend(start_time, year to second) or
date(current) >= t_days.day)
";
if ( $rt )
    $sql .= " AND route.route_id in ( $rt )";
if ( $op )
    $sql .= " AND operator.operator_id in ( $op )";

$sql .="
INTO TEMP t_timetable WITH NO LOG
";

$ds->Execute($sql) or print $sql."<br>".$ds->ErrorMsg(); 

$sql="
CREATE INDEX ix_tttb ON t_timetable ( day, pub_ttb_id );
";
$ds->Execute($sql) or print $sql."<br>".$ds->ErrorMsg(); 


$sql = "";

$sql.= "
SELECT t_timetable.day,
   active_rt.pub_ttb_id,
   active_rt.employee_id,
   active_rt.vehicle_id,
   active_rt_loc.arrival_status,
   active_rt_loc.departure_status,
   active_rt_loc.location_id,
   'ACTIVE' actarc,
   dcd_prediction.*
   FROM dcd_prediction, active_rt, active_rt_loc, t_timetable
   WHERE 1 = 1
   AND t_timetable.day = date(active_rt.actual_start)
   AND active_rt.pub_ttb_id = t_timetable.pub_ttb_id
   AND active_rt.schedule_id = active_rt_loc.schedule_id
   AND dcd_prediction.schedule_id = active_rt_loc.schedule_id
   AND dcd_prediction.rpat_orderby = active_rt_loc.rpat_orderby
   AND extend(prediction, hour to second) between $ftm and $ttm";


if ($loc)
$sql .= " AND active_rt_loc.location_id IN ( $loc )";

if ( strlen($actonly) == 0 )
{

$sql .=" UNION ALL";

$sql .= "
SELECT t_timetable.day,
   archive_rt.pub_ttb_id,
   archive_rt.employee_id,
   archive_rt.vehicle_id,
   archive_rt_loc.arrival_status,
   archive_rt_loc.departure_status,
   archive_rt_loc.location_id,
   'ARCHIVE' actarc,
   dcd_prediction.*
   FROM dcd_prediction, archive_rt, archive_rt_loc, t_timetable
   WHERE 1 = 1
   AND t_timetable.day = date(archive_rt.actual_start)
   AND archive_rt.pub_ttb_id = t_timetable.pub_ttb_id
   AND archive_rt.schedule_id = archive_rt_loc.schedule_id
   AND dcd_prediction.schedule_id = archive_rt_loc.schedule_id
   AND dcd_prediction.rpat_orderby = archive_rt_loc.rpat_orderby
AND extend(prediction, hour to second) between $ftm and $ttm";

if ($loc)
$sql .= " AND archive_rt_loc.location_id IN ( $loc )";

}

$sql .= " INTO TEMP t_acttime WITH NO LOG;
";
$ds->Execute($sql) or print $sql."<br>".$ds->ErrorMsg(); 


$sql="
CREATE INDEX ix_tatb ON t_acttime ( day, pub_ttb_id );
";
$ds->Execute($sql) or print $sql."<br>".$ds->ErrorMsg(); 

$sql="
CREATE INDEX ix_tatb1 ON t_acttime ( schedule_id, rpat_orderby );
";
$ds->Execute($sql) or print $sql."<br>".$ds->ErrorMsg(); 


$sql ="
SELECT schedule_id, rpat_orderby, min(prediction) cleartime
FROM t_acttime
WHERE pred_type = 'X'
GROUP BY 1, 2
INTO TEMP t_cleartimes_only WITH NO LOG
";
$ds->Execute($sql) or print $sql."<br>".$ds->ErrorMsg(); 

$sql ="
SELECT UNIQUE t_acttime.schedule_id, t_acttime.rpat_orderby, cleartime
FROM t_acttime, outer t_cleartimes_only
WHERE t_acttime.schedule_id = t_cleartimes_only.schedule_id
AND t_acttime.rpat_orderby = t_cleartimes_only.rpat_orderby
INTO TEMP t_cleartimes_prep WITH NO LOG
";
$ds->Execute($sql) or print $sql."<br>".$ds->ErrorMsg(); 

$sql ="
SELECT schedule_id, rpat_orderby, '$ipd' initialpred, min(send_time) first_send
FROM t_acttime
WHERE pred_type = 'C'
GROUP BY 1, 2, 3
INTO TEMP t_mintimes WITH NO LOG
";
$ds->Execute($sql) or print $sql."<br>".$ds->ErrorMsg(); 

$sql = "
SELECT UNIQUE t_cleartimes_prep.schedule_id, t_cleartimes_prep.rpat_orderby, cleartime,
INTERVAL(00:00) MINUTE(8) TO SECOND + ( t_acttime.prediction - t_cleartimes_prep.cleartime ) deviation,
INTERVAL(00:00) MINUTE(8) TO SECOND + ( t_acttime.prediction - t_acttime.pub_etd_sent) lateness,
INTERVAL(00:00) MINUTE(8) TO SECOND + ( t_cleartimes_prep.cleartime - t_acttime.pub_etd_sent) devclrpub,
INTERVAL(00:00) MINUTE(8) TO SECOND + ( t_acttime.prediction - t_cleartimes_prep.cleartime + t_cleartimes_prep.cleartime - t_acttime.pub_etd_sent ) validity,
INTERVAL(00:00:00) HOUR TO SECOND + ( t_acttime.prediction - t_cleartimes_prep.cleartime + t_cleartimes_prep.cleartime - t_acttime.pub_etd_sent ) validitypos_hhmmss,
INTERVAL(00:00:00) HOUR TO SECOND - ( t_acttime.prediction - t_cleartimes_prep.cleartime  + t_cleartimes_prep.cleartime - t_acttime.pub_etd_sent ) validityneg_hhmmss,
INTERVAL(00:00:00) HOUR TO SECOND + ( t_acttime.prediction - t_cleartimes_prep.cleartime ) deviationneg_hhmmss,
INTERVAL(00:00:00) HOUR TO SECOND - ( t_acttime.prediction - t_cleartimes_prep.cleartime ) deviationpos_hhmmss
FROM t_acttime,t_mintimes, t_cleartimes_prep
WHERE 1 = 1
AND t_acttime.schedule_id = t_cleartimes_prep.schedule_id
AND t_acttime.rpat_orderby = t_cleartimes_prep.rpat_orderby
AND t_acttime.schedule_id = t_mintimes.schedule_id
AND t_acttime.rpat_orderby = t_mintimes.rpat_orderby
AND t_acttime.send_time = t_mintimes.first_send
INTO TEMP t_cleartimes WITH NO LOG
";

$ds->Execute($sql) or print $sql."<br>".$ds->ErrorMsg(); 


$sql = "
UPDATE t_cleartimes 
SET (validity, validitypos_hhmmss, validityneg_hhmmss ) = 
( deviation, deviationpos_hhmmss, deviationneg_hhmmss )
WHERE devclrpub < '00:00'
";

$ds->Execute($sql) or print $sql."<br>".$ds->ErrorMsg();
